insert into tbl_sanpham(id,nameProduct,imgProduct, describes, status, quantity, discount, percentDiscount, price, view, idDM,idNCC,author)
values(5,'Combo Shin Cậu bé bút chì  Truyện Ngắn 50 tập ( Tái Bản 2019 )','cc010f27ae2201b76f10061fc3677374.jpg',
'<p>Được ph&aacute;t h&agrave;nh lần đầu v&agrave;o năm 1992, bộ truyện sớm g&acirc;y được tiếng vang đối với độc giả Nhật Bản v&agrave; nhiều nước kh&aacute;c tr&ecirc;n thế giới. V&agrave;i năm sau đ&oacute;, loạt phim hoạt h&igrave;nh về cậu b&eacute; Shin cũng được sản xuất v&agrave; ph&aacute;t s&oacute;ng li&ecirc;n tục cho đến b&acirc;y giờ.<br />
 Về h&igrave;nh thức thể hiện, t&aacute;c giả sử dụng b&uacute;t ph&aacute;p đơn giản, thậm ch&iacute; c&oacute; vẻ ng&acirc;y ng&ocirc; hơn so với c&aacute;c bộ manga kh&aacute;c. Nội dung truyện cũng đơn giản: tất cả xoay quanh nh&acirc;n vật ch&iacute;nh l&agrave; cậu b&eacute; Shin 5 tuổi với những mối quan hệ th&acirc;n sơ, bố mẹ, h&agrave;ng x&oacute;m, thầy c&ocirc;, bạn b&egrave;, người quen v&agrave; cả những người kh&ocirc;ng quen.<br />
 Mỗi tập truyện khoảng 120 trang, nhưng cứ thử cầm l&ecirc;n xem, bạn sẽ kh&ocirc;ng thể rời mắt khỏi cuốn s&aacute;ch cho đến tận trang cuối c&ugrave;ng. Với t&agrave;i năng kể chuyện hấp dẫn, t&aacute;c giả đ&atilde; biến c&aacute;c trang s&aacute;ch của m&igrave;nh th&agrave;nh những s&acirc;n chơi ngập tr&agrave;n tiếng cười của những c&ocirc; b&eacute;, cậu b&eacute; hồn nhi&ecirc;n v&agrave; một thế giới tuổi thơ đa sắc m&agrave;u.<br />
 Những b&agrave;i học gi&aacute;o dục nhẹ nh&agrave;ng, thấm th&iacute;a cũng được lồng gh&eacute;p một c&aacute;ch kh&eacute;o l&eacute;o trong từng t&igrave;nh huống truyện. C&oacute; thể Shin l&agrave; một cậu b&eacute; c&aacute; t&iacute;nh, hiếu động. C&oacute; thể những tr&ograve; tinh nghịch của Shin đ&ocirc;i khi qu&aacute; trớn, chẳng chừa một ai. Nhưng sau những sự cố do Shin g&acirc;y ra, người lớn thấy m&igrave;nh cần quan t&acirc;m đến trẻ con nhiều hơn nữa, c&aacute;c bạn đọc nhỏ tuổi chắc hẳn cũng được dịp nh&igrave;n nhận lại bản th&acirc;n, để ph&acirc;n biệt điều tốt điều xấu trong cuộc sống.</p>
 <p>Gi&aacute; sản phẩm tr&ecirc;n Tiki đ&atilde; bao gồm thuế theo luật hiện h&agrave;nh. Tuy nhi&ecirc;n tuỳ v&agrave;o từng loại sản phẩm hoặc phương thức, địa chỉ giao h&agrave;ng m&agrave; c&oacute; thể ph&aacute;t sinh th&ecirc;m chi ph&iacute; kh&aacute;c như ph&iacute; vận chuyển, phụ ph&iacute; h&agrave;ng cồng kềnh, ...</p>
 ',1,10,855000,10,950000, 0,2,1,'Usui Yoshito');
insert into tbl_sanpham(id,nameProduct,imgProduct, describes, status, quantity, discount, percentDiscount, price, view, idDM,idNCC,author)
values(7,'Your Name','dc00eccce4b4e0ec7098ff41df67c686.jpg','<p dir="ltr">Mitsuha là nữ sinh trung học s&ocirc;́ng ở vùng qu&ecirc; hẻo lánh. M&ocirc;̣t ngày nọ, c&ocirc; mơ th&acirc;́y mình ở Tokyo trong một căn ph&ograve;ng xa lạ, biến thành con trai, gặp những người bạn chưa từng quen bi&ecirc;́t.</p>
 <p dir="ltr">Trong khi đ&oacute; ở m&ocirc;̣t nơi khác, Taki, một nam sinh trung học người Tokyo lại mơ th&acirc;́y mình biến thành con gái, s&ocirc;́ng ở vùng qu&ecirc; hẻo lánh.</p>
 <p dir="ltr">Cu&ocirc;́i cùng hai người họ nh&acirc;̣n ra đang bị hoán đ&ocirc;̉i với nhau qua gi&acirc;́c mơ. K&ecirc;̉ từ lúc hai con người xa lạ ấy gặp nhau, bánh xe s&ocirc;́ ph&acirc;̣n bắt đ&acirc;̀u chuy&ecirc;̉n đ&ocirc;̣ng.</p>
 <p dir="ltr">Đ&acirc;y là phi&ecirc;n bản ti&ecirc;̉u thuy&ecirc;́t của b&ocirc;̣ phim hoạt hình Your Name, do ch&iacute;nh đạo diễn Shinkai Makoto chấp bút.</p>
 ',1,25,56250,25,75000,0,2,1,'Shinkai Makoto');
insert into tbl_sanpham(id,nameProduct,imgProduct, describes, status, quantity, discount, percentDiscount, price, view, idDM,idNCC,author)
values(8, 'Về Chuyện Tôi Chuyển Sinh Thành Slime Tập 1','ee89c67084f8fefd5aa817f3e9b11635.jpg','<p>Th&ocirc;ng tin qu&agrave; tặng k&egrave;m:</p>
 <ul>
 	<li>Bản đặc biệt tặng k&egrave;m boolmark, poster khổ lớn, bảng sticker</li>
 	<li>Bản thường tặng k&egrave;m bookmark, postẻ khổ lớn</li>
 </ul>
 <p>*Review &ldquo;Về chuyện t&ocirc;i chuyển sinh th&agrave;nh Slime&rdquo; &ndash; Fuse</p>
 <p>Mikami Satoru &ndash; một nh&acirc;n vi&ecirc;n văn ph&ograve;ng b&igrave;nh thường - đ&atilde; ba mươi bảy c&aacute;i xu&acirc;n xanh nhưng vẫn c&ograve;n l&agrave; một thanh ni&ecirc;n trai t&acirc;n ch&iacute;nh hiệu. Một ng&agrave;y nọ, v&igrave; bảo vệ đồng nghiệp của m&igrave;nh, anh bị một t&ecirc;n đi&ecirc;n cầm dao đ&acirc;m tr&ecirc;n phố. Trong những gi&acirc;y ph&uacute;t cuối đời, khi phải l&ecirc;n đường sang thế giới b&ecirc;n kia trong t&igrave;nh trạng &ldquo;h&agrave;ng c&ograve;n nguy&ecirc;n tem&rdquo; v&agrave; trong đầu anh kh&ocirc;ng ngừng vang l&ecirc;n một giọng n&oacute;i th&ocirc;ng b&aacute;o về một loạt những năng lực kh&oacute; hiểu, anh đ&atilde; thề rằng: Nếu được t&aacute;i sinh, anh nhất định sẽ thay đổi, sẽ chủ động t&aacute;n tỉnh tất cả những người m&agrave; m&igrave;nh gặp, nhiệt t&igrave;nh theo đuổi con mồi rồi lao v&agrave;o &ldquo;ăn sạch&rdquo;&hellip;</p>
 <p>Nhưng trớ tr&ecirc;u thay khi tỉnh lại, Satoru lại kh&ocirc;ng được &ldquo;t&aacute;i sinh&rdquo; th&agrave;nh một anh ch&agrave;ng đẹp trai bảnh bao cuốn h&uacute;t như &yacute; nguyện, m&agrave; lại th&agrave;nh một thứ sinh vật &egrave;o uột trơn tuột, kh&ocirc;ng thể nh&igrave;n được, nghe được, hay n&oacute;i được.</p>
 <p>&ldquo;T&ocirc;i cũng chẳng biết bản th&acirc;n tr&ocirc;ng như thế n&agrave;o. T&ocirc;i cảm thấy cơ thể m&igrave;nh cứ&hellip; trơn tuột, giống như thạch vậy, hệt như h&igrave;nh d&aacute;ng của loại &ldquo;qu&aacute;i vật&rdquo; đ&oacute;. Thực ra t&ocirc;i cũng đ&atilde; nghĩ đến khả năng n&agrave;y từ n&atilde;y rồi.</p>
 <p>T&ocirc;i thực sự đ&atilde;&hellip; được t&aacute;i sinh th&agrave;nh một con slime&hellip;&rdquo;</p>
 <p>Dần d&agrave; chấp nhận số phận rằng bản th&acirc;n đ&atilde; bị biến th&agrave;nh một con Slime kỳ lạ, Mikami Satoru bắt đầu cuộc sống nh&agrave;n tản của m&igrave;nh tại dị giới. Tuy nhi&ecirc;n, từ khi t&igrave;nh cờ gặp được Bạo Phong Long Veldora &ndash; một con qu&aacute;i vật cấp &ldquo;thi&ecirc;n tai&rdquo; v&agrave; nhận được c&aacute;i t&ecirc;n &ldquo;Rimuru Tempest&rdquo; th&igrave; vận mệnh của anh đ&atilde; ho&agrave;n to&agrave;n thay đổi.</p>
 <p>Liệu một người được chuyển sinh v&agrave;o cơ thể của một con slime mất đi th&iacute;nh gi&aacute;c, thị gi&aacute;c sẽ l&agrave;m thế n&agrave;o để sống s&oacute;t tại dị giới đ&acirc;y? V&agrave; d&ugrave; trước khi chết đ&atilde; nhận được hai kỹ năng v&ocirc; song l&agrave; Đại Hiền Giả v&agrave; Kẻ Săn Mồi gi&uacute;p bản th&acirc;n đặc biệt hơn hẳn c&aacute;c con slime th&ocirc;ng thường nhưng Rimuru lại chưa nhận ra điều đ&oacute;, anh sẽ l&agrave;m g&igrave; để bảo vệ bản th&acirc;n v&agrave; thực hiện được mục ti&ecirc;u &ldquo;ăn sạch&rdquo; của m&igrave;nh trong thế giới đầy rẫy những hiểm nguy n&agrave;y?</p>
 <p>&ldquo;Về chuyện t&ocirc;i chuyển sinh th&agrave;nh Slime&rdquo; l&agrave; series light novel được chắp b&uacute;t bởi Fuse v&agrave; vẽ minh họa bởi Mizt Vah đ&atilde; g&acirc;y được tiếng vang lớn với 20 triệu bản s&aacute;ch được b&aacute;n ra tại Nhật. C&ugrave;ng nội dung l&ocirc;i cuốn v&agrave; phần minh họa đỉnh cao, series đ&igrave;nh đ&aacute;m n&agrave;y hứa hẹn sẽ đưa người đọc v&agrave;o một cuộc h&agrave;nh tr&igrave;nh hồi hộp hấp dẫn đến nghẹt thở ngay từ tập đầu ti&ecirc;n!<br />
 <br />
 &nbsp;</p>
